package main.java;/** * class for matrix positions */import java.lang.*;  // for class Exceptionpublic class InvalidOperationException extends RuntimeException {            /**	 * 	 */	private static final long serialVersionUID = 1L;		/**         * Constructs a <code>InvalidOperationException</code>          * with no detail message.         */        public InvalidOperationException() {                System.out.println("Invalid Operation error, Please restart");        }        /**         * Constructs a <code>DimensionException</code> with the specified          * detail message.          *         * @param   s   the detail message.         */        public InvalidOperationException(String message) {                super( message );        }}